programs.vertex:
#version 100

attribute vec3 v_Position;

uniform mat4 P; // Projection matrix
uniform mat4 V; // View matrix
uniform mat4 M; // Model matrix

void main() {
    gl_Position = P * V * M * vec4(v_Position, 1);
}

programs.fragment:
#version 100

precision mediump float;

uniform vec4 color;

void main() {
    gl_FragColor = color;
}

javascript:
// Computes a a 4x4 translation matrix given
// a 3-dimensional translation vector.
function makeTranslationMatrix(c, translation) {
    var x = translation[0];
    var y = translation[1];
    var z = translation[2];

    var M = c.math.mat4(
        1, 0, 0, x,
        0, 1, 0, y,
        0, 0, 1, z,
        0, 0, 0, 1
    );

    // Since we wrote the matrix in the usual mathematical
    // order (i.e. row-wise) but OpenGL expects column-wise
    // order, we transpose the matrix here.
    c.math.mat4.transpose(M, M);
    return M;
}

// Computes a 4x4 (uniform) scaling
// matrix given a scaling factor.
function makeScalingMatrix(c, factor) {
    var f = factor;

    var M = c.math.mat4(
        f, 0, 0, 0,
        0, f, 0, 0,
        0, 0, f, 0,
        0, 0, 0, 1
    );

    return M;
}

// Computes a 4x4 rotation matrix given a
// rotation axis and a rotation angle.
function makeRotationMatrix(c, axis, angle) {
    // Just in case, normalize the given axis vector.
    c.math.vec3.normalize(axis, axis);

    // TASK:
    // We use utility functions here to generate a quaternion
    // representing a rotation around a given axis with a given
    // angle, and then convert it to a 4x4 matrix. Your task
    // is to replace the following four lines with a manual
    // computation of (1) a suitable quaternion (x, y, z, w), and
    // (2) an explicit conversion of the quaternion to a 4x4 matrix.
    // If you write the matrix in mathematical order, don't forget
    // to transpose it before returning it (analogously to the
    // "makeTranslationMatrix" function). You should only use
    // plain vectors (vec4), matrices (mat4), Math.cos, Math.sin,
    // and usual scalar multiplication (e.g. 2*x*y).
    var q = c.math.quat.create();
    c.math.quat.setAxisAngle(q, axis, angle);
    var M = c.math.mat4.create();
    c.math.mat4.fromQuat(M, q);

    return M;
}

// Initialization
function init(c) {
    // Enable depth buffering to make sure parts
    // of objects behind other objects are hidden.
    c.gl.enable(c.gl.DEPTH_TEST);
    // Make a generic sphere for later use.
    this.sphere = new c.models.IcoSphere(c, 0.2, 4);
}

// Render function, called each render cycle
function render(c) {
    // Set up the viewport (i.e. the area
    // where stuff will be drawn.
    var w = c.gl.canvas.clientWidth;
    var h = c.gl.canvas.clientHeight;
    c.gl.viewport(0, 0, w, h);

    // "time" contains a (float) timestamp looping
    // in the range [0, 2pi). We can use this when
    // specifying transformations to get animation.
    var time = (new Date).getTime() / 1000 % (2 * Math.PI);

    // Set up the projection matrix. We use a utility
    // function to set up a perspective transformation.
    var P = c.math.mat4.create();
    c.math.mat4.perspective(P, 45 * Math.PI / 180.0, w / h, 0.1, 100.0);

    // Set up the view matrix. We rotate and translate
    // the world to get the effect of a camera looking
    // down at the scene slightly tilted downwards.
    var V = c.math.mat4.create();
    c.math.mat4.multiply(V, V, makeTranslationMatrix(c, c.math.vec3(0, 0, -4)));
    c.math.mat4.multiply(V, V, makeRotationMatrix(c, c.math.vec3(1, 0, 0), 0.7));

    // Set up the first model matrix for rendering the
    // red "sun". We only apply a scaling to it.
    var M = c.math.mat4.create();
    c.math.mat4.multiply(M, M, makeScalingMatrix(c, 3.0));

    // Render the sun in red color with the specified
    // matrices (see vertex and fragment shaders for
    // how P, V, M, and color are used.
    c.view(this.view);
    this.sphere.render(c, {uniforms: {
        color: c.math.vec4(0.8, 0, 0, 1),
        P: P, V: V, M: M
    }});

    // For rendering the green "planet", we reset our
    // matrix first to undo the scaling (you could also
    // scale it by the inverse factor). Then we apply
    // a translation and rotation. Note that the rotation
    // depends on the "time" variable.
    c.math.mat4.identity(M);
    c.math.mat4.multiply(M, M, makeRotationMatrix(c, c.math.vec3(0, 1, 0), time));
    c.math.mat4.multiply(M, M, makeTranslationMatrix(c, c.math.vec3(1.2, 0, 0)));

    // Render the planet using the updated model matrix.
    c.view(this.view);
    this.sphere.render(c, {uniforms: {
        color: c.math.vec4(0, 0.8, 0, 1),
        P: P, V: V, M: M
    }});

    // TASK:
    // Render a blue "moon" that revolves around the green planet.
    // The center of revolution shall be located in the center
    // of the green planet. The moon shall be a little smaller than
    // the planet. See the exercise description for an example
    // how the result should look like
}
