programs.vertex:
#version 100

attribute vec3 v_Position;
// uncomment the next line to define the new color attribute
attribute vec4 v_Color;

uniform mat4 modelview;
uniform mat4 projection;

varying vec4 f_Color;

void main() {
    gl_Position = projection * modelview * vec4(v_Position, 1);
    // remove constant color in order to pass correct values
    // this is just written this way in order to for the example to render anything
    //f_Color = vec4(0.0, 0.5, 1.0, 1.0);//v_Color;
    f_Color = v_Color;
}

